import './App.css';
import axios from 'axios'
import React, { Component } from 'react';
import ProductList from './Components/ProductsList';

const api = axios.create({
  baseURL:'https://localhost:44394/api/products/product'
})
const api2 = axios.create({
  baseURL:'https://localhost:44394/api/user/user'
})
const api3 = axios.create({
  baseURL: 'https://localhost:44394/api/authentication/login'
})
const token = 'c275b4e7-8674-4246-b49f-9a5b0ca7d83b'

class App extends Component {
  constructor(){
    super();
    this.state = {
      Products: [],
      CurrentUser: [],
    }
  }
  componentDidMount(){
    this.getProducts()
    this.getUser(token)
  }
  getProducts = async () => {
    let data = await api.get('/').then(({ data }) => data)
    this.setState({ Products : data })
    console.log(this.state.Products)
  }
  getUser = async (token) => {
    let data = await api2.get('/', {headers:{"Authorization" : `Bearer ${token}`}}).then(({ data }) => data)
    this.setState({ CurrentUser : data })
    console.log(this.state.CurrentUser)
  }
  SignInUser = async (event) => {
    event.preventDefault()
    let res = await api3.post('/',{username:event.target.front.value,
    password:event.target.back.value,})
      console.log(res)
  }
render() {
  if (!this.state.CurrentUser){
    return(
      <form className='StackForm' onSubmit = {(event) => this.SignInUser(event)}>
      <h3>Sign in:</h3>
    <label htmlfor="username">UserName: </label>
    <input type = "text" id="username" name="username"/><br/>
    <label htmlfor="password">Password: </label>
    <input type = "text" id="password" name="password"/><br/>
      <button type="submit">Submit</button>
    </form>
    )
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>Hello World!</h1>
      </header>
      <ProductList Products={this.state.Products}/>
    </div>
  );
}
}

export default App;
