import './App.css';
import axios from 'axios'
import React, { Component } from 'react';
import ProductList from './Components/ProductsList';

const api = axios.create({
  baseURL:'https://localhost:44394/api/products/product'
})
const api2 = axios.create({
  baseURL:'https://localhost:44394/api/user/user'
})
const api3 = axios.create({
  baseURL: 'https://localhost:44394/api/authentication/login'
})
const token = localStorage.getItem('data')

class App extends Component {
  constructor(){
    super();
    this.state = {
      Products: [],
      CurrentUser: [],
    }
    this.reviewsGetData = this.reviewsGetData.bind(this);
  }
  componentDidMount(){
    this.getProducts()
    this.getUser(token)
  }
  getProducts = async () => {
    let data = await api.get('/').then(({ data }) => data)
    this.setState({ Products : data })
  }
  getUser = async (token) => {
    let data = await api2.get('/', {headers:{"Authorization" : `Bearer ${token}`}}).then(({ data }) => data)
    this.setState({ CurrentUser : data })
  }
  SignInUser = async (event) => {
    event.preventDefault()
    let res = await api3.post('/',{username:event.target.username.value,
    password:event.target.password.value,})
    localStorage.setItem('data', res.data.token)
    this.getUser(token)
    window.location.reload()
  }

  reviewsGetData = async () => {
    this.state.reviews.map(review => this.renderGetData(review.ProductId))
  }

  renderGetData = async(id) => {//needs to be at higher level.
    try {
        let response = await axios.get(`https://localhost:44394/api/reviews/${id}`)
        this.setState({reviewsProduct: [response.data]});
        console.log(this.state.reviewsProduct)
    } catch (err) {
        console.log(err);
    } 
}


  
render() {
  if (this.state.CurrentUser.length === 0)
    return(
      <form className='StackForm' onSubmit = {(event) => this.SignInUser(event)}>
      <h3>Sign in:</h3>
    <label htmlfor="username">Username: </label>
    <input type = "text" id="username" name="username"/><br/>
    <label htmlfor="password">Password: </label>
    <input type = "text" id="password" name="password"/><br/>
      <button type="submit">Submit</button>
    </form>
    )
  else
    return (
      <div className="App">
        <header className="App-header">
          <h1>Hello World!</h1>
        </header>
        <h2>Product List</h2>
        <ProductList products={this.state.Products} reviews={() => this.reviewsGetData()}/>
      </div>
    );
  
  
}
}

export default App;
