import axios from 'axios'
import React, { Component } from 'react';
import Rating from './Rating'
import './ProductDetail.css';


  
function ProductDetail(product, handleProductSelect) {
    return (
        <tr className = 'Product-item'>
            {product.name}
            <br></br>
            {product.description}
            <br></br>
            {product.price}
            <br></br>
            <button className='review-button' onSubmit={() => handleProductSelect(product)}>See Reviews</button>
            <br></br>
        </tr>
            )

}

export default ProductDetail;
