    children,
    type, 
    onClick,
    buttonStyle,
    buttonSize
}) => {
    const checkButtonStyle = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0]

    const checkButtonSize = SIZES.includes(buttonSize) ? buttonSizee : SIZES[0]

    return (
        <button className={'btn ${checkButtonStle} ${checkButtonSize}'} onClick={onClick}
        type={type}>
            {children}
        </button>
    )
}
